func (c *current) onFlags1() (any, error) {
	text := string(c.text)
	hasU := false
	hasV := false
	for _, ch := range text {
		if ch == 'u' {
			hasU = true
		}
		if ch == 'v' {
			hasV = true
		}
	}
	if hasU && hasV {
		return nil, fmt.Errorf("flags 'u' and 'v' cannot be used together")
	}
	return text, nil
}

